const retry = (fn, ms=1000,maxRetries=5,url) =>
     new Promise((resolve,reject) => { 
        var retries=0;
        fn(url)
        .then(resolve)
        .catch(() => {
            setTimeout(() => {
                console.log('retrying ...' + url);
                ++retries;
                if(retries==maxRetries) {
                    return reject('maximum retries exceeded');
                }
                retry(fn, ms,maxRetries,url).then(resolve);
            }, ms);
        })
});


